mapper.select:
	SELECT *
	FROM (
		SELECT /*+INDEX_DESC(review idx_review_regdate) */
			ROWNUM RN, REVIEW.*
		FROM REVIEW

		WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
		AND RE_DATE > '19/01/01'
 	
		
		WHERE SCORE = #{score}
		
		)

	WHERE RN > #{criteria.pageNum}-1 * #{criteria.amount}

SELECT COUNT(*)
FROM REVIEW

WHERE SCORE = #{score}

SELECT MEMBER_ID
FROM SH_USER
WHERE USER_KEY = #{user_key}

SELECT member_id
FROM sh_user
WHERE user_key = #{user_key}

