mapper.select:
		SELECT MAX(board_num)
		FROM (
   		SELECT /*+INDEX(community idx_regdate) */
    	board_num
    	FROM community
    	
   		WHERE regdate > '19/01/01' and TEAM_NAME = #{TEAM_NAME}
		) WHERE board_num < #{BOARD_NUM}

		SELECT MIN(board_num)
		FROM (
   		SELECT /*+INDEX(community idx_regdate) */
    	board_num
    	FROM community
    	
   		WHERE regdate > '19/01/01' and TEAM_NAME = #{TEAM_NAME}
		) WHERE board_num > #{BOARD_NUM}

mapper.update:
UPDATE community
SET hit = hit + 1
WHERE board_num = #{BOARD_NUM}
