mapper.select:
			
     		SELECT *
     		FROM
     			( SELECT *
       			  FROM shop_order 
       			  WHERE user_key = #{key}
            	  		AND cancel = 0
       			  ORDER BY order_num desc )
     		WHERE order_num <= ${total} - (#{pagingVO.pageNum} - 1) * #{pagingVO.amount} 
     			AND order_num > ${total} - #{pagingVO.pageNum} * #{pagingVO.amount}

        SELECT *
        FROM
            ( SELECT rownum rn, order_num, or_date, items, pymntamnt, stts 
              FROM shop_order 
              WHERE user_key = #{key}
            	  AND cancel = 0
			
				  AND stts IN
				
					#{item}
				
			
       		  ORDER BY order_num desc )
       	
        WHERE rn > (#{pagingVO.pageNum} - 1) * #{pagingVO.amount}
            AND rn <= #{pagingVO.pageNum} * #{pagingVO.amount}

		
		    SELECT rownum rn, rv_num, title, re_date, re_hit
     		FROM	
				( SELECT rv_num, title, re_date, re_hit
			  	  FROM review
			   	  WHERE user_key = #{key}
       		  	  ORDER BY rv_num desc )
     		 WHERE rv_num <= ${total} - (#{pagingVO.pageNum} - 1) * #{pagingVO.amount}
     		 	AND rv_num > ${total} - #{pagingVO.pageNum} * #{pagingVO.amount}

			
     		SELECT *
     		FROM
     			( SELECT *
       			  FROM onetoone 
       			  WHERE user_key = #{key}
       			  ORDER BY one_one_num desc )
     		WHERE one_one_num <= ${total} - (#{pagingVO.pageNum} - 1) * #{pagingVO.amount} 
     			AND one_one_num > ${total} - #{pagingVO.pageNum} * #{pagingVO.amount}

			
     		SELECT *
     		FROM
     			( SELECT *
       			  FROM qna 
       			  WHERE user_key = #{key}
       			  ORDER BY qna_num desc )
     		WHERE qna_num <= ${total} - (#{pagingVO.pageNum} - 1) * #{pagingVO.amount} 
     			AND qna_num > ${total} - #{pagingVO.pageNum} * #{pagingVO.amount}

SELECT savings, stts
FROM shop_order
WHERE user_key = #{key}

mapper.update:
UPDATE shop_order 
SET stts = 4 
WHERE order_num = #{order_num}

mapper.delete:
DELETE 
FROM address
WHERE user_key = #{key} AND num = #{num}

