mapper.update:
UPDATE shop_order 
SET stts = 4, cancel = 1 
WHERE order_num = #{order_num}

UPDATE gd_glove
SET stock = stock - #{QNTTY}
WHERE glove_num = #{GLOVE_NUM}

UPDATE gd_bat
SET stock = stock - #{QNTTY}
WHERE bat_num = #{BAT_NUM}

UPDATE gd_uniform
SET stock = stock - #{QNTTY}
WHERE uniform_num = #{UNIFORM_NUM}

UPDATE gd_shoes
SET stock = stock - #{QNTTY}
WHERE shoes_num = #{SHOES_NUM}

UPDATE gd_ball
SET stock = stock - #{QNTTY}
WHERE ball_num = #{BALL_NUM}

UPDATE shop_order
SET tid = #{tid}
WHERE order_num = #{order_num}

UPDATE goods 
SET sales = sales + #{qntty}
WHERE goods_num = #{goods_num}

UPDATE gd_glove
SET stock = stock + #{QNTTY}
WHERE glove_num = #{GLOVE_NUM}

UPDATE gd_bat
SET stock = stock + #{QNTTY}
WHERE bat_num = #{BAT_NUM}

UPDATE gd_uniform
SET stock = stock + #{QNTTY}
WHERE uniform_num = #{UNIFORM_NUM}

UPDATE gd_shoes
SET stock = stock + #{QNTTY}
WHERE shoes_num = #{SHOES_NUM}

UPDATE gd_ball
SET stock = stock + #{QNTTY}
WHERE ball_num = #{BALL_NUM}

UPDATE savings
SET savings_used = savings_used + #{useSavings}
WHERE order_num = #{order_num}

UPDATE sh_user
SET savings = savings - #{useSavings}
WHERE user_key = #{user_key}
